import React from 'react';

const STATUS_MAP = {
  available: { label: 'متاحة', color: 'green' },
  occupied: { label: 'مشغولة', color: 'red' },
  maintenance: { label: 'صيانة', color: 'yellow' },
  reserved: { label: 'محجوزة', color: 'orange' },
};

export default function RoomCard({ room }) {
  const s = (room.status || 'available').toLowerCase();
  const st = STATUS_MAP[s] || { label: (room.status || 'متاحة'), color: 'gray' };

  const colorClasses = {
    green: 'border-green-200 bg-green-50',
    red: 'border-red-200 bg-red-50',
    yellow: 'border-yellow-200 bg-yellow-50',
    orange: 'border-orange-200 bg-orange-50',
    gray: 'border-gray-200 bg-white',
  };

  return (
    <div className={`min-w-[160px] rounded border p-4 relative ${colorClasses[st.color]}`}>
      <div className="absolute -top-2 left-3 w-3 h-3 rounded-full" style={{ background: st.color === 'green' ? '#34d399' : st.color === 'red' ? '#fb7185' : st.color === 'yellow' ? '#f6e05e' : st.color === 'orange' ? '#fb923c' : '#cbd5e1' }} />
      <div className="text-sm text-gray-500">{room.room_code || room.room_number}</div>
      <div className="font-semibold mt-2">{room.room_type_name || 'غرفة'}</div>
      <div className="text-xs text-gray-600 mt-1">{st.label}</div>
    </div>
  );
}
