import React, { useEffect, useMemo, useState } from 'react';
import { supabase } from '../supabaseClient';

const daysOfWeek = (startDate) => {
  const days = [];
  const d = new Date(startDate);
  for (let i = 0; i < 7; i++) {
    const di = new Date(d);
    di.setDate(d.getDate() + i);
    days.push(di);
  }
  return days;
};

const statusColor = (s) => {
  const v = String(s||'').toLowerCase();
  if (v==='pending') return '#f59e0b';
  if (v==='confirmed') return '#10b981';
  if (v==='checked_in') return '#3b82f6';
  if (v==='checked_out') return '#6b7280';
  if (v==='canceled' || v==='cancelled') return '#ef4444';
  if (v==='no_show') return '#f59e0b';
  return '#9ca3af';
};

export default function Calendar() {
  const [rooms, setRooms] = useState([]);
  const [events, setEvents] = useState([]);
  const [loading, setLoading] = useState(true);
  const [start, setStart] = useState(() => {
    const d = new Date();
    d.setHours(0,0,0,0);
    return d.toISOString().slice(0,10);
  });
  const [drag, setDrag] = useState(null); // { res, startDate }

  const loadRooms = async () => {
    setLoading(true);
    try {
      const { data, error } = await supabase.from('rooms_overview').select('*').order('room_code');
      if (error) throw error;
      setRooms(data || []);
    } catch (e) {
      console.error('Calendar load failed', e);
      setRooms([]);
    } finally {
      setLoading(false);
    }
  };

  const loadEvents = async (startDate, endDate) => {
    try {
      const { data, error } = await supabase.rpc('get_calendar_reservations', { p_start: startDate, p_end: endDate });
      if (error) throw error;
      setEvents(data || []);
    } catch (e) {
      console.error('Load calendar reservations failed', e);
      setEvents([]);
    }
  };

  useEffect(() => { loadRooms(); }, []);
  useEffect(() => {
    const end = new Date(start);
    end.setDate(end.getDate() + 6);
    loadEvents(start, end.toISOString().slice(0,10));
  }, [start]);

  const days = daysOfWeek(start);

  return (
    <div className="p-8" dir="rtl">
      <div className="flex items-center justify-between mb-6">
        <div>
          <h2 className="text-2xl font-bold">الرزنامة الأسبوعية</h2>
          <p className="text-sm text-gray-500">عرض مبدئي لحالة الغرف عبر الأسبوع</p>
        </div>
        <div className="flex items-center gap-2">
          <input className="border rounded px-3 py-2" type="date" value={start} onChange={e=>setStart(e.target.value)} />
        </div>
      </div>

      <div className="overflow-auto bg-white rounded shadow">
        <table className="min-w-full text-sm">
          <thead>
            <tr>
              <th className="border-b px-3 py-2 text-right w-40">الغرفة</th>
              {days.map((d,i)=> (
                <th key={i} className="border-b px-3 py-2 text-center w-32">{d.toLocaleDateString('ar-EG', { weekday: 'long', day: 'numeric', month: 'numeric' })}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <tr><td colSpan={8} className="px-3 py-4 text-center">تحميل...</td></tr>
            ) : rooms.map(r => (
              <tr key={r.id}>
                <td className="border-t px-3 py-2">{r.room_code} – {r.room_type_name_ar}</td>
                {days.map((d,i)=> {
                  const dayStr = d.toISOString().slice(0,10);
                  const ev = events.find(e => e.room_id === r.id && dayStr >= e.start_date && dayStr < e.end_date);
                  const bg = ev ? statusColor(ev.status) : '#e5e7eb';
                  const title = ev ? `${ev.guest_name} • ${ev.status}` : 'فارغ';
                  return (
                    <td key={i} className="border-t px-3 py-2 select-none"
                        onMouseDown={() => { if (ev) setDrag({ res: ev, startDate: dayStr }); }}
                        onMouseUp={async () => {
                          if (drag && drag.res && drag.res.room_id === r.id) {
                            try {
                              if (drag.startDate !== dayStr) {
                                const newEnd = dayStr; // simple: drop sets new end
                                const { error } = await supabase.rpc('extend_reservation', { p_reservation_id: drag.res.id, p_new_check_out: newEnd });
                                if (error) throw error;
                                const end = new Date(start); end.setDate(end.getDate() + 6);
                                await loadEvents(start, end.toISOString().slice(0,10));
                              }
                            } catch(e) {
                              console.error('Drag/resize failed', e);
                              alert('تعذّر تعديل التاريخ بالسحب: '+(e.message||e));
                            } finally {
                              setDrag(null);
                            }
                          }
                          // Move across rooms: drop on a different row to relocate reservation keeping nights
                          else if (drag && drag.res) {
                            try {
                              const nights = (new Date(drag.res.end_date) - new Date(drag.res.start_date)) / (1000*60*60*24);
                              const newStart = dayStr;
                              const endDate = new Date(newStart);
                              endDate.setDate(endDate.getDate() + nights);
                              const newEnd = endDate.toISOString().slice(0,10);
                              const { error } = await supabase.rpc('move_reservation', {
                                p_reservation_id: drag.res.id,
                                p_new_room_id: r.id,
                                p_new_check_in: newStart,
                                p_new_check_out: newEnd,
                              });
                              if (error) throw error;
                              const end = new Date(start); end.setDate(end.getDate() + 6);
                              await loadEvents(start, end.toISOString().slice(0,10));
                            } catch(e) {
                              console.error('Move failed', e);
                              alert('تعذّر نقل الحجز: '+(e.message||e));
                            } finally { setDrag(null); }
                          }
                        }}
                        onMouseLeave={() => { /* ignore */ }}
                    >
                      <div className="h-5 rounded" style={{ backgroundColor: bg }} title={title}></div>
                    </td>
                  );
                })}
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="mt-4 text-xs text-gray-500">اسحب تاريخ البداية/النهاية بالأعلى لتحديث الأسبوع. الألوان حسب حالة الحجز.</div>
    </div>
  );
}
