import React, { useEffect, useMemo, useState } from 'react';
import { supabase } from '../supabaseClient';

const statusOptions = [
  { value: 'pending', label: 'قيد الانتظار' },
  { value: 'confirmed', label: 'مؤكد' },
  { value: 'checked_in', label: 'تم الدخول' },
  { value: 'checked_out', label: 'تم الخروج' },
  { value: 'cancelled', label: 'ملغي' },
  { value: 'no_show', label: 'لم يحضر' },
];

export default function ReservationModal({ initialData, onClose, onSave }) {
  const [saving, setSaving] = useState(false);
  const [guests, setGuests] = useState([]);
  const [rooms, setRooms] = useState([]);

  const [form, setForm] = useState({
    guest_id: null,
    room_id: null,
    check_in_date: '',
    check_out_date: '',
    guests_count: 1,
    status: 'confirmed',
    nightly_rate: null,
    total_amount: null,
    amount_paid: 0,
    currency: 'SAR',
    payment_method: 'cash',
    special_requests: '',
    notes: '',
  });

  useEffect(()=>{
    const loadOptions = async ()=>{
      const [g, r] = await Promise.all([
        supabase.from('guests_overview').select('id, full_name').order('full_name'),
        supabase.from('rooms_overview').select('id, name, room_number').order('room_number')
      ]);
      setGuests(g.data||[]);
      setRooms(r.data||[]);
    };
    loadOptions();
  }, []);

  useEffect(()=>{
    if (initialData) {
      setForm({
        guest_id: initialData.guest_id,
        room_id: initialData.room_id,
        check_in_date: initialData.check_in_date,
        check_out_date: initialData.check_out_date,
        guests_count: initialData.guests_count ?? 1,
        status: initialData.status ?? 'confirmed',
        nightly_rate: initialData.nightly_rate ?? null,
        total_amount: initialData.total_amount ?? null,
        amount_paid: initialData.amount_paid ?? 0,
        currency: initialData.currency ?? 'SAR',
        payment_method: initialData.payment_method ?? 'cash',
        special_requests: initialData.special_requests ?? '',
        notes: initialData.notes ?? '',
      });
    }
  }, [initialData]);

  const nights = useMemo(()=>{
    if (!form.check_in_date || !form.check_out_date) return 0;
    const start = new Date(form.check_in_date);
    const end = new Date(form.check_out_date);
    const diff = (end - start) / (1000*60*60*24);
    return diff > 0 ? diff : 0;
  }, [form.check_in_date, form.check_out_date]);

  useEffect(()=>{
    if (form.nightly_rate != null && nights>0) {
      const total = Math.round(Number(form.nightly_rate) * nights * 100) / 100;
      setForm(f=>({ ...f, total_amount: total }));
    }
  }, [form.nightly_rate, nights]);

  const remaining = useMemo(()=>{
    const t = Number(form.total_amount||0);
    const p = Number(form.amount_paid||0);
    return Math.max(0, Math.round((t - p) * 100) / 100);
  }, [form.total_amount, form.amount_paid]);

  const checkAvailability = async () => {
    const { data, error } = await supabase.rpc('check_room_availability', {
      p_room_id: form.room_id,
      p_check_in: form.check_in_date,
      p_check_out: form.check_out_date,
      p_reservation_id: initialData?.id || null,
    });
    if (error) throw error;
    return data; // expected boolean
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setSaving(true);
      if (!form.guest_id || !form.room_id || !form.check_in_date || !form.check_out_date) {
        alert('يرجى تعبئة جميع الحقول الأساسية');
        setSaving(false);
        return;
      }
      // Optional: availability check if RPC exists, otherwise rely on trigger
      try {
        const ok = await checkAvailability();
        if (ok === false) {
          alert('الغرفة غير متاحة في هذه الفترة');
          setSaving(false);
          return;
        }
      } catch {
        // silently ignore if RPC not available
      }

      const payload = { ...form };
      onSave(payload);
    } catch(e) {
      console.error(e);
      alert('حدث خطأ غير متوقع');
    } finally {
      setSaving(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-black/40 z-50 flex items-center justify-center" dir="rtl">
      <div className="bg-white rounded shadow-lg w-full max-w-3xl max-h-[90vh] overflow-hidden">
        <div className="px-4 py-3 border-b flex items-center justify-between sticky top-0 bg-white">
          <h3 className="font-bold">{initialData ? 'تعديل حجز' : 'حجز جديد'}</h3>
          <button className="text-gray-500 hover:text-gray-700" onClick={onClose}>إغلاق</button>
        </div>
        <form onSubmit={handleSubmit} className="p-4 space-y-4 overflow-y-auto">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="block text-sm mb-1">النزيل</label>
              <select className="border rounded w-full px-3 py-2" value={form.guest_id||''} onChange={e=>setForm(f=>({ ...f, guest_id: e.target.value||null }))}>
                <option value="">اختر النزيل</option>
                {guests.map(g=> <option key={g.id} value={g.id}>{g.full_name}</option>)}
              </select>
            </div>
            <div>
              <label className="block text-sm mb-1">الغرفة</label>
              <select className="border rounded w-full px-3 py-2" value={form.room_id||''} onChange={e=>setForm(f=>({ ...f, room_id: e.target.value||null }))}>
                <option value="">اختر الغرفة</option>
                {rooms.map(r=> <option key={r.id} value={r.id}>{r.room_number || r.name || r.id}</option>)}
              </select>
            </div>
            <div>
              <label className="block text-sm mb-1">تاريخ الدخول</label>
              <input type="date" className="border rounded w-full px-3 py-2" value={form.check_in_date} onChange={e=>setForm(f=>({ ...f, check_in_date: e.target.value }))} />
            </div>
            <div>
              <label className="block text-sm mb-1">تاريخ الخروج</label>
              <input type="date" className="border rounded w-full px-3 py-2" value={form.check_out_date} onChange={e=>setForm(f=>({ ...f, check_out_date: e.target.value }))} />
            </div>
            <div>
              <label className="block text-sm mb-1">عدد النزلاء</label>
              <input type="number" min={1} className="border rounded w-full px-3 py-2" value={form.guests_count} onChange={e=>setForm(f=>({ ...f, guests_count: Number(e.target.value)||1 }))} />
            </div>
            <div>
              <label className="block text-sm mb-1">الحالة</label>
              <select className="border rounded w-full px-3 py-2" value={form.status} onChange={e=>setForm(f=>({ ...f, status: e.target.value }))}>
                {statusOptions.map(s=> <option key={s.value} value={s.value}>{s.label}</option>)}
              </select>
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <label className="block text-sm mb-1">سعر الليلة</label>
              <input type="number" className="border rounded w-full px-3 py-2" value={form.nightly_rate ?? ''} onChange={e=>setForm(f=>({ ...f, nightly_rate: e.target.value===''? null : Number(e.target.value) }))} />
            </div>
            <div>
              <label className="block text-sm mb-1">الإجمالي</label>
              <input type="number" className="border rounded w-full px-3 py-2" value={form.total_amount ?? ''} onChange={e=>setForm(f=>({ ...f, total_amount: e.target.value===''? null : Number(e.target.value) }))} />
            </div>
            <div>
              <label className="block text-sm mb-1">المدفوع</label>
              <input type="number" className="border rounded w-full px-3 py-2" value={form.amount_paid ?? 0} onChange={e=>setForm(f=>({ ...f, amount_paid: Number(e.target.value)||0 }))} />
            </div>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div>
              <label className="block text-sm mb-1">المتبقي</label>
              <div className="border rounded w-full px-3 py-2 bg-gray-50">{remaining}</div>
            </div>
            <div>
              <label className="block text-sm mb-1">العملة</label>
              <input className="border rounded w-full px-3 py-2" value={form.currency} onChange={e=>setForm(f=>({ ...f, currency: e.target.value }))} />
            </div>
            <div>
              <label className="block text-sm mb-1">طريقة الدفع</label>
              <input className="border rounded w-full px-3 py-2" value={form.payment_method} onChange={e=>setForm(f=>({ ...f, payment_method: e.target.value }))} />
            </div>
          </div>

          <div>
            <label className="block text-sm mb-1">طلبات خاصة</label>
            <textarea className="border rounded w-full px-3 py-2" rows={2} value={form.special_requests} onChange={e=>setForm(f=>({ ...f, special_requests: e.target.value }))} />
          </div>
          <div>
            <label className="block text-sm mb-1">ملاحظات</label>
            <textarea className="border rounded w-full px-3 py-2" rows={2} value={form.notes} onChange={e=>setForm(f=>({ ...f, notes: e.target.value }))} />
          </div>

          <div className="flex items-center justify-between border-t pt-3">
            <div className="text-sm text-gray-500">ليالي: {nights}</div>
            <div className="flex gap-2">
              <button type="button" className="px-4 py-2 border rounded" onClick={onClose}>إلغاء</button>
              <button type="submit" className="px-4 py-2 bg-green-600 hover:bg-green-700 text-white rounded" disabled={saving}>{saving? 'جارٍ الحفظ...' : 'حفظ'}</button>
            </div>
          </div>
        </form>
      </div>
    </div>
  );
}
