import React, { useState } from 'react';

export default function GuestModal({ initialData, onClose, onSave }) {
  const [full_name, setFullName] = useState(initialData?.full_name || '');
  const [phone, setPhone] = useState(initialData?.phone || '');
  const [national_id, setNationalId] = useState(initialData?.national_id || '');
  const [saving, setSaving] = useState(false);
  const [errors, setErrors] = useState({});

  const validate = () => {
    const e = {};
    if (!full_name || String(full_name).trim().length === 0) e.full_name = 'اكتب الاسم الكامل.';
    setErrors(e);
    return Object.keys(e).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!validate()) return;
    setSaving(true);
    try {
      await onSave({ full_name, phone, national_id });
    } finally {
      setSaving(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50" dir="rtl">
      <div className="bg-white rounded-lg shadow-xl w-full max-w-xl">
        <div className="px-6 py-4 border-b">
          <h3 className="text-xl font-bold">{initialData ? 'تعديل النزيل' : 'إضافة نزيل جديد'}</h3>
        </div>
        <form onSubmit={handleSubmit} className="px-6 py-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div>
              <label className="text-sm">الاسم الكامل *</label>
              <input className="mt-1 border rounded w-full px-3 py-2" value={full_name} onChange={e=>{ setFullName(e.target.value); setErrors(prev=>({ ...prev, full_name: undefined })); }} placeholder="الاسم" />
              {errors.full_name && <div className="text-red-600 text-xs mt-1">{errors.full_name}</div>}
            </div>
            <div>
              <label className="text-sm">الهاتف</label>
              <input className="mt-1 border rounded w-full px-3 py-2" value={phone} onChange={e=>setPhone(e.target.value)} placeholder="رقم الهاتف" />
            </div>
            <div>
              <label className="text-sm">رقم الهوية</label>
              <input className="mt-1 border rounded w-full px-3 py-2" value={national_id} onChange={e=>setNationalId(e.target.value)} placeholder="رقم الهوية" />
            </div>
          </div>
          <div className="flex justify-end gap-3 mt-6">
            <button type="button" onClick={onClose} className="bg-gray-200 hover:bg-gray-300 text-gray-800 px-4 py-2 rounded">إلغاء</button>
            <button type="submit" disabled={saving} className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded">{saving ? 'جارٍ الحفظ...' : 'حفظ'}</button>
          </div>
        </form>
      </div>
    </div>
  );
}
