import React, { useState } from 'react';
import { DashboardIcon, BuildingIcon, FloorIcon, RoomTypeIcon, OccupancyIcon, CardIcon, ScheduleIcon, UserIcon, SettingsIcon } from './Icons';

export default function Sidebar({ onNavigate }) {
  const [collapsed, setCollapsed] = useState(() => {
    try {
      return window.localStorage.getItem('sidebar_collapsed') === '1';
    } catch (_) {
      return false;
    }
  });

  const toggleCollapsed = () => {
    setCollapsed((prev) => {
      const next = !prev;
      try {
        window.localStorage.setItem('sidebar_collapsed', next ? '1' : '0');
      } catch (_) {}
      return next;
    });
  };
  const nav = (page, e) => {
    if (onNavigate) {
      e.preventDefault && e.preventDefault();
      try { console.log('Sidebar nav ->', page); } catch (_) {}
      onNavigate(page);
      // تحديث العنوان فقط بدون إعادة تحميل الصفحة
      try { window.history.pushState({}, '', `/${page}`); } catch (_) {}
    }
  };

  return (
    <aside className={`bg-[#16213e] text-white ${collapsed ? 'w-16' : 'w-72'} min-h-screen flex flex-col justify-between shadow-xl relative transition-all duration-200`} dir="rtl">
      <button
        type="button"
        onClick={toggleCollapsed}
        className="absolute -right-3 top-24 z-20 w-6 h-6 rounded-full bg-[#1f2a48] border border-[#0f172a] flex items-center justify-center text-xs hover:bg-[#223459]"
        title={collapsed ? 'توسيع الشريط الجانبي' : 'طي الشريط الجانبي'}
      >
        {collapsed ? '›' : '‹'}
      </button>
      <div>
        <div className="p-6 text-2xl font-bold border-b border-[#1f2a48] flex items-center justify-between gap-2">
          <span className="truncate">{collapsed ? 'الكيلاني' : 'فندق الكيلاني'}</span>
          {!collapsed && (
            <span className="text-xs font-normal mt-1 text-[#ffd700] whitespace-nowrap">نظام إدارة متكامل</span>
          )}
        </div>
        <nav className="mt-6 flex flex-col gap-2 px-2 text-base">
          <a
            href="/dashboard"
            onClick={(e)=>nav('dashboard', e)}
            className={`py-2 px-3 rounded flex items-center ${collapsed ? 'justify-center' : 'gap-3'} bg-[#1f2a48] text-[#ffd700]`}
          >
            <DashboardIcon className="w-5 h-5"/>
            {!collapsed && <span>لوحة التحكم</span>}
          </a>
          <a
            href="/buildings"
            onClick={(e)=>nav('buildings', e)}
            className={`py-2 px-3 rounded hover:bg-[#1f2a48] flex items-center ${collapsed ? 'justify-center' : 'gap-3'}`}
          >
            <BuildingIcon className="w-5 h-5"/>
            {!collapsed && <span>المباني</span>}
          </a>
          <a
            href="/floors"
            onClick={(e)=>nav('floors', e)}
            className={`py-2 px-3 rounded hover:bg-[#1f2a48] flex items-center ${collapsed ? 'justify-center' : 'gap-3'}`}
          >
            <FloorIcon className="w-5 h-5"/>
            {!collapsed && <span>الطوابق</span>}
          </a>
          <a
            href="/rooms"
            onClick={(e)=>nav('rooms', e)}
            className={`py-2 px-3 rounded hover:bg-[#1f2a48] flex items-center ${collapsed ? 'justify-center' : 'gap-3'}`}
          >
            <RoomTypeIcon className="w-5 h-5"/>
            {!collapsed && <span>الغرف</span>}
          </a>
          <a
            href="/room-types"
            onClick={(e)=>nav('room-types', e)}
            className={`py-2 px-3 rounded hover:bg-[#1f2a48] flex items-center ${collapsed ? 'justify-center' : 'gap-3'}`}
          >
            <RoomTypeIcon className="w-5 h-5"/>
            {!collapsed && <span>أنواع الغرف</span>}
          </a>
          <a
            href="/reservations"
            onClick={(e)=>nav('reservations', e)}
            className={`py-2 px-3 rounded hover:bg-[#1f2a48] flex items-center ${collapsed ? 'justify-center' : 'gap-3'}`}
          >
            <ScheduleIcon className="w-5 h-5"/>
            {!collapsed && <span>الحجوزات الشاملة</span>}
          </a>
          <a
            href="/guests"
            onClick={(e)=>nav('guests', e)}
            className={`py-2 px-3 rounded hover:bg-[#1f2a48] flex items-center ${collapsed ? 'justify-center' : 'gap-3'}`}
          >
            <UserIcon className="w-5 h-5"/>
            {!collapsed && <span>النزلاء</span>}
          </a>

          <a
            href="/vacant-rooms"
            className={`py-2 px-3 rounded hover:bg-[#1f2a48] flex items-center ${collapsed ? 'justify-center' : 'gap-3'}`}
          >
            <RoomTypeIcon className="w-5 h-5"/>
            {!collapsed && <span>الغرف الفارغة</span>}
          </a>
          <a
            href="/smart-cards"
            className={`py-2 px-3 rounded hover:bg-[#1f2a48] flex items-center ${collapsed ? 'justify-center' : 'gap-3'}`}
          >
            <CardIcon className="w-5 h-5"/>
            {!collapsed && <span>البطاقات الذكية</span>}
          </a>
          {/* أزيلت روابط التقويم/التسكين المنفصلة لصالح صفحة الحجوزات الشاملة */}
          <a
            href="/users"
            className={`py-2 px-3 rounded hover:bg-[#1f2a48] flex items-center ${collapsed ? 'justify-center' : 'gap-3'}`}
          >
            <UserIcon className="w-5 h-5"/>
            {!collapsed && <span>المستخدمون</span>}
          </a>
          <a
            href="/settings"
            className={`py-2 px-3 rounded hover:bg-[#1f2a48] flex items-center ${collapsed ? 'justify-center' : 'gap-3'}`}
          >
            <SettingsIcon className="w-5 h-5"/>
            {!collapsed && <span>إعدادات النظام</span>}
          </a>
        </nav>
      </div>
      <div className={`p-4 border-t border-[#1f2a48] flex ${collapsed ? 'flex-col items-center gap-1' : 'flex-col gap-2'}`}>
        {!collapsed && <div className="text-sm">System Administrator</div>}
        <button className="bg-red-600 hover:bg-red-700 text-white py-2 px-4 rounded text-sm w-full">خروج</button>
      </div>
    </aside>
  );
}
