import React, { useEffect, useState } from 'react';
import { supabase } from '../supabaseClient';

const daysOfWeek = (startDate) => {
  const days = [];
  const d = new Date(startDate);
  for (let i = 0; i < 7; i++) {
    const di = new Date(d);
    di.setDate(d.getDate() + i);
    days.push(di);
  }
  return days;
};

const statusClass = (s) => {
  switch (s) {
    case 'available': return 'bg-status-available';
    case 'reserved': return 'bg-status-reserved';
    case 'occupied': return 'bg-status-occupied';
    case 'maintenance': return 'bg-status-maintenance';
    default: return 'bg-gray-300';
  }
};

export default function Calendar() {
  const [rooms, setRooms] = useState([]);
  const [loading, setLoading] = useState(true);
  const [start, setStart] = useState(() => {
    const d = new Date();
    d.setHours(0,0,0,0);
    return d.toISOString().slice(0,10);
  });

  const loadRooms = async () => {
    setLoading(true);
    try {
      const { data, error } = await supabase.from('rooms_overview').select('*').order('room_code');
      if (error) throw error;
      setRooms(data || []);
    } catch (e) {
      console.error('Calendar load failed', e);
      setRooms([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { loadRooms(); }, []);

  const days = daysOfWeek(start);

  return (
    <div className="p-8" dir="rtl">
      <div className="flex items-center justify-between mb-6">
        <div>
          <h2 className="text-2xl font-bold">الرزنامة الأسبوعية</h2>
          <p className="text-sm text-gray-500">عرض مبدئي لحالة الغرف عبر الأسبوع</p>
        </div>
        <div className="flex items-center gap-2">
          <input className="border rounded px-3 py-2" type="date" value={start} onChange={e=>setStart(e.target.value)} />
        </div>
      </div>

      <div className="overflow-auto bg-white rounded shadow">
        <table className="min-w-full text-sm">
          <thead>
            <tr>
              <th className="border-b px-3 py-2 text-right w-40">الغرفة</th>
              {days.map((d,i)=> (
                <th key={i} className="border-b px-3 py-2 text-center w-32">{d.toLocaleDateString('ar-EG', { weekday: 'long', day: 'numeric', month: 'numeric' })}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {loading ? (
              <tr><td colSpan={8} className="px-3 py-4 text-center">تحميل...</td></tr>
            ) : rooms.map(r => (
              <tr key={r.id}>
                <td className="border-t px-3 py-2">{r.room_code} – {r.room_type_name_ar}</td>
                {days.map((_,i)=> (
                  <td key={i} className="border-t px-3 py-2">
                    <div className={`h-4 rounded ${statusClass(r.status)}`} title={`الحالة: ${r.status}`}></div>
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="mt-4 text-xs text-gray-500">ملاحظة: للعرض الكامل نحتاج جدول الحجوزات/الإشغال لتلوين كل يوم بدقّة.</div>
    </div>
  );
}
