import React, { useContext, useEffect, useState } from 'react';
import { supabase } from '../supabaseClient';
import { AuthContext } from '../App.jsx';

export default function CashboxModal({ onClose, onDone }) {
  const currentUser = useContext(AuthContext);
  const [balance, setBalance] = useState(0);
  const [operation, setOperation] = useState('deposit'); // deposit | withdraw
  const [amount, setAmount] = useState('');
  const [description, setDescription] = useState('');
  const [loading, setLoading] = useState(false);

  const loadBalance = async () => {
    try {
      const { data, error } = await supabase.rpc('get_cashbox_balance');
      if (error) throw error;
      setBalance(Number(data || 0));
    } catch (e) {
      console.error('load cashbox balance error', e);
    }
  };

  useEffect(() => {
    loadBalance();
  }, []);

  const handleSubmit = async () => {
    const amt = Number(amount || 0);
    if (!amt || amt <= 0) {
      alert('من فضلك أدخل مبلغًا صالحًا');
      return;
    }
    setLoading(true);
    try {
      const direction = operation === 'deposit' ? 'income' : 'expense';
      let categoryId = null;
      try {
        const targetName = direction === 'income' ? 'إيرادات أخرى' : 'مصروفات أخرى';
        const { data: cats } = await supabase
          .from('accounting_categories')
          .select('id,name,type')
          .eq('type', direction === 'income' ? 'income' : 'expense')
          .eq('name', targetName)
          .limit(1);
        if (cats && cats.length > 0) {
          categoryId = cats[0].id;
        }
      } catch (_) {}

      const payload = {
        tx_date: new Date().toISOString().slice(0, 10),
        direction,
        category_id: categoryId,
        amount: amt,
        payment_method: 'cash',
        bank_account_id: null,
        source_type: 'manual',
        reservation_id: null,
        description: description || (operation === 'deposit' ? 'عملية إيداع في الخزنة' : 'عملية سحب من الخزنة'),
      };
      if (currentUser && currentUser.id) {
        payload.created_by = currentUser.id;
      }

      const { error } = await supabase.from('accounting_transactions').insert(payload);
      if (error) throw error;
      await loadBalance();
      onDone && onDone();
    } catch (e) {
      console.error('cashbox submit error', e);
      alert('تعذر حفظ عملية الخزنة: ' + (e.message || e));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="fixed inset-0 bg-black/40 flex items-center justify-center p-4" dir="rtl">
      <div className="bg-white w-full max-w-md rounded-xl shadow-lg overflow-hidden">
        <div className="px-4 py-3 border-b bg-amber-50 flex items-center justify-between">
          <div>
            <div className="font-bold text-amber-900">إدارة الخزنة</div>
            <div className="text-xs text-amber-700">عرض الرصيد الحالي وتسجيل إيداع أو سحب</div>
          </div>
          <button type="button" className="text-sm text-gray-500" onClick={onClose}>إغلاق</button>
        </div>

        <div className="p-4 flex flex-col gap-4">
          <div className="bg-amber-100 border border-amber-200 rounded-lg px-4 py-3 text-center">
            <div className="text-xs text-amber-700 mb-1">الرصيد الحالي</div>
            <div className="text-2xl font-bold text-amber-900">{balance} جنيه</div>
          </div>

          <div className="flex flex-col gap-2">
            <label className="text-sm font-medium">نوع العملية</label>
            <select
              className="border rounded px-3 py-2 text-sm"
              value={operation}
              onChange={(e) => setOperation(e.target.value)}
            >
              <option value="deposit">إيداع (+)</option>
              <option value="withdraw">سحب (−)</option>
            </select>
          </div>

          <div className="flex flex-col gap-2">
            <label className="text-sm font-medium">المبلغ</label>
            <input
              type="number"
              min="0"
              className="border rounded px-3 py-2 text-sm"
              value={amount}
              onChange={(e) => setAmount(e.target.value)}
            />
          </div>

          <div className="flex flex-col gap-2">
            <label className="text-sm font-medium">الوصف (اختياري)</label>
            <textarea
              rows={3}
              className="border rounded px-3 py-2 text-sm resize-none"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
          </div>
        </div>

        <div className="px-4 py-3 border-t bg-gray-50 flex items-center justify-between">
          <button
            type="button"
            className="px-3 py-2 border rounded text-sm"
            onClick={onClose}
          >
            إلغاء
          </button>
          <button
            type="button"
            className="px-4 py-2 rounded text-sm text-white bg-amber-600 hover:bg-amber-700 disabled:opacity-60"
            onClick={handleSubmit}
            disabled={loading}
          >
            {loading ? 'جارٍ الحفظ...' : 'حفظ العملية'}
          </button>
        </div>
      </div>
    </div>
  );
}
