import React from 'react';

export default function RoomCard({ room }) {
  const statusColor = () => {
    const s = (room.status || 'available').toLowerCase();
    if (s === 'available') return 'border-green-200 bg-green-50';
    if (s === 'occupied') return 'border-red-200 bg-red-50';
    if (s === 'maintenance') return 'border-yellow-200 bg-yellow-50';
    if (s === 'reserved') return 'border-orange-200 bg-orange-50';
    return 'border-gray-200 bg-white';
  };

  return (
    <div className={`min-w-[160px] rounded border p-4 ${statusColor()}`}>
      <div className="text-sm text-gray-500">{room.room_code || room.room_number}</div>
      <div className="font-semibold mt-2">{room.room_type_name || 'غرفة'}</div>
      <div className="text-xs text-gray-600 mt-1">{(room.status || 'available').charAt(0).toUpperCase() + (room.status || 'available').slice(1)}</div>
    </div>
  );
}
