import React, { useEffect, useMemo, useState } from 'react';
import { supabase } from '../supabaseClient';
import CashboxModal from '../components/CashboxModal.jsx';
import AccountingIncomeModal from '../components/AccountingIncomeModal.jsx';
import AccountingExpenseModal from '../components/AccountingExpenseModal.jsx';
import BankAccountsModal from '../components/BankAccountsModal.jsx';

export default function Accounting() {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({
    todayIncome: 0,
    todayExpense: 0,
    todayNet: 0,
    monthIncome: 0,
    monthExpense: 0,
    monthNet: 0,
  });

  const [showCashbox, setShowCashbox] = useState(false);
  const [showIncome, setShowIncome] = useState(false);
  const [showExpense, setShowExpense] = useState(false);
  const [showBanks, setShowBanks] = useState(false);

  const todayLabel = useMemo(() => {
    try {
      return new Date().toLocaleDateString('ar-EG', {
        weekday: 'long',
        day: 'numeric',
        month: 'long',
        year: 'numeric',
      });
    } catch (_) {
      return '';
    }
  }, []);

  useEffect(() => {
    async function loadStats() {
      setLoading(true);
      try {
        const today = new Date();
        const yyyyMmDd = today.toISOString().slice(0, 10);
        const monthStart = new Date(today.getFullYear(), today.getMonth(), 1).toISOString().slice(0, 10);

        const { data: tx } = await supabase
          .from('accounting_transactions')
          .select('tx_date,direction,amount')
          .gte('tx_date', monthStart)
          .lte('tx_date', yyyyMmDd);

        let todayIncome = 0;
        let todayExpense = 0;
        let monthIncome = 0;
        let monthExpense = 0;

        (tx || []).forEach((row) => {
          const amt = Number(row.amount || 0);
          if (!amt) return;
          const isIncome = row.direction === 'income';
          if (row.tx_date === yyyyMmDd) {
            if (isIncome) todayIncome += amt; else todayExpense += amt;
          }
          if (isIncome) monthIncome += amt; else monthExpense += amt;
        });

        setStats({
          todayIncome,
          todayExpense,
          todayNet: todayIncome - todayExpense,
          monthIncome,
          monthExpense,
          monthNet: monthIncome - monthExpense,
        });
      } catch (e) {
        console.error('loadStats error', e);
      } finally {
        setLoading(false);
      }
    }

    loadStats();
  }, []);

  const refreshAfterTx = () => {
    // ÿ®ÿπÿØ ÿ£Ÿä ÿπŸÖŸÑŸäÿ© ŸÖÿßŸÑŸäÿ©ÿå ŸÜÿπŸäÿØ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÖŸÑÿÆÿµÿßÿ™
    try {
      const evt = new Event('accounting-tx-updated');
      window.dispatchEvent(evt);
    } catch (_) {}
  };

  useEffect(() => {
    const handler = () => {
      // ÿ•ÿπÿßÿØÿ© ÿ™ÿ≠ŸÖŸäŸÑ ŸÜŸÅÿ≥ ÿßŸÑŸÖŸÜÿ∑ŸÇ ÿ®ÿØŸàŸÜ ÿ™ŸÉÿ±ÿßÿ± ÿßŸÑŸÉŸàÿØ ÿ®ÿßŸÑŸÉÿßŸÖŸÑ
      (async () => {
        try {
          const today = new Date();
          const yyyyMmDd = today.toISOString().slice(0, 10);
          const monthStart = new Date(today.getFullYear(), today.getMonth(), 1).toISOString().slice(0, 10);
          const { data: tx } = await supabase
            .from('accounting_transactions')
            .select('tx_date,direction,amount')
            .gte('tx_date', monthStart)
            .lte('tx_date', yyyyMmDd);

          let todayIncome = 0;
          let todayExpense = 0;
          let monthIncome = 0;
          let monthExpense = 0;

          (tx || []).forEach((row) => {
            const amt = Number(row.amount || 0);
            if (!amt) return;
            const isIncome = row.direction === 'income';
            if (row.tx_date === yyyyMmDd) {
              if (isIncome) todayIncome += amt; else todayExpense += amt;
            }
            if (isIncome) monthIncome += amt; else monthExpense += amt;
          });

          setStats({
            todayIncome,
            todayExpense,
            todayNet: todayIncome - todayExpense,
            monthIncome,
            monthExpense,
            monthNet: monthIncome - monthExpense,
          });
        } catch (e) {
          console.error('reload stats error', e);
        }
      })();
    };

    try {
      window.addEventListener('accounting-tx-updated', handler);
    } catch (_) {}
    return () => {
      try { window.removeEventListener('accounting-tx-updated', handler); } catch (_) {}
    };
  }, []);

  const renderDashboardTab = () => (
    <>
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
        <div className="bg-emerald-50 border border-emerald-100 rounded-lg p-4 text-center">
          <div className="text-xs text-emerald-700 mb-1">ÿ•Ÿäÿ±ÿßÿØÿßÿ™ ÿßŸÑŸäŸàŸÖ</div>
          <div className="text-xl font-bold text-emerald-900">{stats.todayIncome} ÿ¨ŸÜŸäŸá</div>
        </div>
        <div className="bg-rose-50 border border-rose-100 rounded-lg p-4 text-center">
          <div className="text-xs text-rose-700 mb-1">ŸÖÿµÿ±ŸàŸÅÿßÿ™ ÿßŸÑŸäŸàŸÖ</div>
          <div className="text-xl font-bold text-rose-900">{stats.todayExpense} ÿ¨ŸÜŸäŸá</div>
        </div>
        <div className="bg-blue-50 border border-blue-100 rounded-lg p-4 text-center">
          <div className="text-xs text-blue-700 mb-1">ÿµÿßŸÅŸä ÿßŸÑŸäŸàŸÖ</div>
          <div className="text-xl font-bold text-blue-900">{stats.todayNet} ÿ¨ŸÜŸäŸá</div>
        </div>
        <div className="bg-indigo-50 border border-indigo-100 rounded-lg p-4 text-center">
          <div className="text-xs text-indigo-700 mb-1">ÿµÿßŸÅŸä ÿßŸÑÿ¥Ÿáÿ± ÿßŸÑÿ≠ÿßŸÑŸä</div>
          <div className="text-xl font-bold text-indigo-900">{stats.monthNet} ÿ¨ŸÜŸäŸá</div>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
        <button
          type="button"
          onClick={() => setShowCashbox(true)}
          className="bg-amber-50 hover:bg-amber-100 border border-amber-200 rounded-lg p-4 flex flex-col items-center justify-center gap-2 transition"
        >
          <span className="text-2xl">üí∞</span>
          <span className="font-semibold text-amber-900">ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿÆÿ≤ŸÜÿ©</span>
        </button>
        <button
          type="button"
          onClick={() => setShowIncome(true)}
          className="bg-emerald-50 hover:bg-emerald-100 border border-emerald-200 rounded-lg p-4 flex flex-col items-center justify-center gap-2 transition"
        >
          <span className="text-2xl">üìà</span>
          <span className="font-semibold text-emerald-900">ÿ™ÿ≥ÿ¨ŸäŸÑ ÿ•Ÿäÿ±ÿßÿØ</span>
        </button>
        <button
          type="button"
          onClick={() => setShowExpense(true)}
          className="bg-rose-50 hover:bg-rose-100 border border-rose-200 rounded-lg p-4 flex flex-col items-center justify-center gap-2 transition"
        >
          <span className="text-2xl">üìâ</span>
          <span className="font-semibold text-rose-900">ÿ™ÿ≥ÿ¨ŸäŸÑ ŸÖÿµÿ±ŸàŸÅ</span>
        </button>
        <button
          type="button"
          onClick={() => setShowBanks(true)}
          className="bg-sky-50 hover:bg-sky-100 border border-sky-200 rounded-lg p-4 flex flex-col items-center justify-center gap-2 transition"
        >
          <span className="text-2xl">üè¶</span>
          <span className="font-semibold text-sky-900">ÿ≠ÿ≥ÿßÿ®ÿßÿ™ ÿßŸÑÿ®ŸÜŸàŸÉ</span>
        </button>
      </div>
    </>
  );

  const renderTransactionsTab = () => (
    <div className="text-sm text-gray-500 p-4 bg-white rounded-lg border">
      ÿ≥Ÿäÿ™ŸÖ ŸáŸÜÿß ÿπÿ±ÿ∂ ÿ≥ÿ¨ŸÑ ŸÉÿßŸÖŸÑ ŸÑŸÉŸÑ ÿßŸÑŸÖÿπÿßŸÖŸÑÿßÿ™ ÿßŸÑŸÖÿßŸÑŸäÿ© ŸÖÿπ ŸÅŸÑÿßÿ™ÿ± Ÿàÿ®ÿ≠ÿ´ (ÿ≥ŸÜŸÇŸàŸÖ ÿ®ÿ®ŸÜÿßÿ¶Ÿá ŸÅŸä ÿÆÿ∑Ÿàÿ© ŸÑÿßÿ≠ŸÇÿ© ÿ®ÿπÿØ ÿßÿπÿ™ŸÖÿßÿØ ÿßŸÑÿ¨ÿ≤ÿ° ÿßŸÑÿ≠ÿßŸÑŸä).
    </div>
  );

  const renderAccountsTreeTab = () => (
    <div className="text-sm text-gray-500 p-4 bg-white rounded-lg border">
      ÿ≥Ÿäÿ™ŸÖ ŸáŸÜÿß ÿπÿ±ÿ∂ ŸÅÿ¶ÿßÿ™ ÿßŸÑÿ•Ÿäÿ±ÿßÿØÿßÿ™ ŸàÿßŸÑŸÖÿµÿ±ŸàŸÅÿßÿ™ ÿ®ÿ¥ŸÉŸÑ ŸÖŸÜÿ∏ŸÖ (ÿ¥ÿ¨ÿ±ÿ© ŸÖÿ®ÿ≥ÿ∑ÿ©) ŸÖÿπ ÿ•ŸÖŸÉÿßŸÜŸäÿ© ÿ•ÿ∂ÿßŸÅÿ© ŸÅÿ¶ÿßÿ™ ÿ¨ÿØŸäÿØÿ©.
    </div>
  );

  const renderReportsTab = () => (
    <div className="text-sm text-gray-500 p-4 bg-white rounded-lg border">
      ÿ≥Ÿäÿ™ŸÖ ŸáŸÜÿß ŸÑÿßÿ≠ŸÇŸãÿß ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑÿ™ŸÇÿßÿ±Ÿäÿ± ÿßŸÑŸÖÿßŸÑŸäÿ© (ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑÿØÿÆŸÑÿå ŸÖŸÑÿÆÿµ ŸÅÿ™ÿ±ÿ©ÿå ÿ•ŸÑÿÆ).
    </div>
  );

  const renderActiveTab = () => {
    switch (activeTab) {
      case 'dashboard':
        return renderDashboardTab();
      case 'transactions':
        return renderTransactionsTab();
      case 'accounts-tree':
        return renderAccountsTreeTab();
      case 'reports':
        return renderReportsTab();
      default:
        return renderDashboardTab();
    }
  };

  return (
    <div className="flex flex-col gap-6 p-6 bg-gray-50 min-h-screen" dir="rtl">
      <div className="flex justify-between items-center">
        <div className="flex gap-2">
          <button className="bg-yellow-400 text-white px-4 py-2 rounded">ŸÜÿ≥ÿÆÿ© ÿßÿ≠ÿ™Ÿäÿßÿ∑Ÿäÿ©</button>
          <button className="bg-white border rounded p-2" title="ÿßŸÑÿ•ÿ¥ÿπÿßÿ±ÿßÿ™"><span role="img" aria-label="ÿ™ŸÜÿ®ŸäŸá">üîî</span></button>
        </div>
        <div className="text-gray-600 text-sm">{todayLabel}</div>
      </div>

      <div>
        <h1 className="text-2xl font-bold text-gray-800 mb-1">ÿßŸÑŸÜÿ∏ÿßŸÖ ÿßŸÑŸÖÿ≠ÿßÿ≥ÿ®Ÿä ÿßŸÑÿ∞ŸÉŸä</h1>
        <p className="text-sm text-gray-500">ÿ•ÿØÿßÿ±ÿ© ŸÉÿßŸÖŸÑÿ© ŸÑŸÑŸÖÿπŸÑŸàŸÖÿßÿ™ ÿßŸÑŸÖÿßŸÑŸäÿ© ŸàÿßŸÑŸÖÿπÿßŸÖŸÑÿßÿ™ ÿßŸÑŸäŸàŸÖŸäÿ© ŸÑŸÑŸÅŸÜÿØŸÇ.</p>
      </div>

      <div className="flex flex-wrap gap-2 bg-white rounded-lg p-2 border">
        <button
          type="button"
          onClick={() => setActiveTab('dashboard')}
          className={`px-4 py-2 rounded text-sm ${activeTab === 'dashboard' ? 'bg-blue-600 text-white' : 'bg-gray-50 text-gray-700 hover:bg-gray-100'}`}
        >
          ŸÑŸàÿ≠ÿ© ÿßŸÑŸÖÿπŸÑŸàŸÖÿßÿ™
        </button>
        <button
          type="button"
          onClick={() => setActiveTab('transactions')}
          className={`px-4 py-2 rounded text-sm ${activeTab === 'transactions' ? 'bg-blue-600 text-white' : 'bg-gray-50 text-gray-700 hover:bg-gray-100'}`}
        >
          ÿßŸÑŸÖÿπÿßŸÖŸÑÿßÿ™
        </button>
        <button
          type="button"
          onClick={() => setActiveTab('accounts-tree')}
          className={`px-4 py-2 rounded text-sm ${activeTab === 'accounts-tree' ? 'bg-blue-600 text-white' : 'bg-gray-50 text-gray-700 hover:bg-gray-100'}`}
        >
          ÿ¥ÿ¨ÿ±ÿ© ÿßŸÑÿ≠ÿ≥ÿßÿ®ÿßÿ™
        </button>
        <button
          type="button"
          onClick={() => setActiveTab('reports')}
          className={`px-4 py-2 rounded text-sm ${activeTab === 'reports' ? 'bg-blue-600 text-white' : 'bg-gray-50 text-gray-700 hover:bg-gray-100'}`}
        >
          ÿßŸÑÿ™ŸÇÿßÿ±Ÿäÿ±
        </button>
      </div>

      {loading ? (
        <div className="bg-white rounded-lg p-6 text-center text-gray-500 border">ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ŸÖŸÑÿÆÿµ ÿßŸÑŸÖÿ≠ÿßÿ≥ÿ®ÿ©...</div>
      ) : (
        renderActiveTab()
      )}

      {showCashbox && (
        <CashboxModal
          onClose={() => setShowCashbox(false)}
          onDone={() => {
            setShowCashbox(false);
            refreshAfterTx();
          }}
        />
      )}

      {showIncome && (
        <AccountingIncomeModal
          onClose={() => setShowIncome(false)}
          onDone={() => {
            setShowIncome(false);
            refreshAfterTx();
          }}
        />
      )}

      {showExpense && (
        <AccountingExpenseModal
          onClose={() => setShowExpense(false)}
          onDone={() => {
            setShowExpense(false);
            refreshAfterTx();
          }}
        />
      )}

      {showBanks && (
        <BankAccountsModal
          onClose={() => setShowBanks(false)}
        />
      )}
    </div>
  );
}
