import React from 'react';

export default function RoomTypeCard({ type, onEdit }) {
  const feats = Array.isArray(type.features) ? type.features : [];
  return (
    <div className="bg-white rounded-lg shadow-md border border-gray-200 overflow-hidden" dir="rtl">
      <div className="h-1 bg-gradient-to-r from-purple-500 to-indigo-500" />
      <div className="px-4 pt-4">
        <div className="text-blue-700 font-extrabold text-xl">{Number(type.base_price || 0)}</div>
        <div className="text-[11px] text-gray-500">ليلة/غرفة</div>
      </div>
      <div className="px-4 py-3">
        <div className="flex items-center justify-between">
          <div>
            <div className="font-semibold text-[15px]">{type.name_ar}</div>
            <div className="text-[11px] text-gray-500">{type.name}</div>
          </div>
          {type.code && (
            <span className="text-[10px] px-2 py-1 rounded bg-indigo-100 text-indigo-800 uppercase tracking-wide">{type.code}</span>
          )}
        </div>
        <div className="mt-2 text-[11px] text-gray-600">عدد الأشخاص الأقصى: {Number(type.max_guests || 0)}</div>
        {feats.length > 0 && (
          <div className="mt-2 flex flex-wrap gap-1">
            {feats.map((f,i)=> (
              <span key={i} className="text-[10px] px-2 py-1 rounded bg-gray-100 text-gray-700">{f}</span>
            ))}
          </div>
        )}
        {type.description && (
          <div className="mt-3 text-[11px] text-gray-600">{type.description}</div>
        )}
      </div>
      <div className="px-4 py-3 border-t bg-gray-50">
        <button onClick={onEdit} className="bg-amber-500 hover:bg-amber-600 text-white px-4 py-2 rounded text-sm w-full">تعديل ✍️</button>
      </div>
    </div>
  );
}
