import React, { useState } from 'react';
import Sidebar from './components/Sidebar';
import Dashboard from './pages/Dashboard';
import Buildings from './pages/Buildings';

function App() {
  const [activePage, setActivePage] = useState(() => {
    try {
      const p = window.location.pathname.replace(/^\//, '') || 'dashboard';
      return p;
    } catch (e) {
      return 'dashboard';
    }
  });

  const renderPage = () => {
    if (activePage === 'buildings') return <Buildings />;
    return <Dashboard />;
  };

  return (
    <div className="flex min-h-screen bg-[#f5f6fa] font-sans" dir="rtl">
      <Sidebar onNavigate={(page) => setActivePage(page)} />
      <main className="flex-1">
        {renderPage()}
      </main>
    </div>
  );
}

export default App;
