import React, { useState } from 'react';
import { DashboardIcon, BuildingIcon, FloorIcon, RoomTypeIcon, OccupancyIcon, CardIcon, ScheduleIcon, UserIcon, SettingsIcon } from './Icons';

export default function Sidebar({ onNavigate }) {
  const [collapsed, setCollapsed] = useState(() => {
    try {
      return window.localStorage.getItem('sidebar_collapsed') === '1';
    } catch (_) {
      return false;
    }
  });

  const toggleCollapsed = () => {
    setCollapsed((prev) => {
      const next = !prev;
      try {
        window.localStorage.setItem('sidebar_collapsed', next ? '1' : '0');
      } catch (_) {}
      return next;
    });
  };
  const nav = (page, e) => {
    if (onNavigate) {
      e.preventDefault && e.preventDefault();
      try { console.log('Sidebar nav ->', page); } catch (_) {}
      onNavigate(page);
      // تحديث العنوان فقط بدون إعادة تحميل الصفحة
      try { window.history.pushState({}, '', `/${page}`); } catch (_) {}
    }
  };

  return (
    <aside className={`bg-[#16213e] text-white ${collapsed ? 'w-14' : 'w-64'} h-screen flex flex-col justify-between shadow-xl relative transition-all duration-200 sticky top-0`} dir="rtl">
      <button
        type="button"
        onClick={toggleCollapsed}
        className="absolute -right-3 top-24 z-20 w-8 h-8 rounded-full bg-[#facc15] border border-[#92400e] flex items-center justify-center hover:bg-[#fde68a] shadow-lg"
        title={collapsed ? 'توسيع الشريط الجانبي' : 'طي الشريط الجانبي'}
      >
        <svg
          className="w-4 h-4 text-[#111827]"
          viewBox="0 0 24 24"
          xmlns="http://www.w3.org/2000/svg"
        >
          {collapsed ? (
            // سهم يشير للداخل (توسيع)
            <path d="M10 6l6 6-6 6" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" />
          ) : (
            // سهم يشير للخارج (طي)
            <path d="M14 6l-6 6 6 6" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" />
          )}
        </svg>
      </button>
      <div>
        <div className="p-6 text-2xl font-bold border-b border-[#1f2a48] flex items-center justify-between gap-2">
          <span className="truncate">فندق الكيلاني</span>
          {!collapsed && (
            <span className="text-xs font-normal mt-1 text-[#ffd700] whitespace-nowrap">نظام إدارة متكامل</span>
          )}
        </div>
        <nav className="mt-6 flex flex-col gap-2 px-2 text-base">
          <a
            href="/dashboard"
            onClick={(e)=>nav('dashboard', e)}
            className="py-2 px-3 rounded flex items-center gap-3 bg-[#1f2a48] text-[#ffd700]"
          >
            <DashboardIcon className="w-5 h-5"/>
            <span className="truncate">لوحة التحكم</span>
          </a>
          <a
            href="/buildings"
            onClick={(e)=>nav('buildings', e)}
            className="py-2 px-3 rounded hover:bg-[#1f2a48] flex items-center gap-3"
          >
            <BuildingIcon className="w-5 h-5"/>
            <span className="truncate">المباني</span>
          </a>
          <a
            href="/floors"
            onClick={(e)=>nav('floors', e)}
            className="py-2 px-3 rounded hover:bg-[#1f2a48] flex items-center gap-3"
          >
            <FloorIcon className="w-5 h-5"/>
            <span className="truncate">الطوابق</span>
          </a>
          <a
            href="/rooms"
            onClick={(e)=>nav('rooms', e)}
            className="py-2 px-3 rounded hover:bg-[#1f2a48] flex items-center gap-3"
          >
            <RoomTypeIcon className="w-5 h-5"/>
            <span className="truncate">الغرف</span>
          </a>
          <a
            href="/room-types"
            onClick={(e)=>nav('room-types', e)}
            className="py-2 px-3 rounded hover:bg-[#1f2a48] flex items-center gap-3"
          >
            <RoomTypeIcon className="w-5 h-5"/>
            <span className="truncate">أنواع الغرف</span>
          </a>
          <a
            href="/reservations"
            onClick={(e)=>nav('reservations', e)}
            className="py-2 px-3 rounded hover:bg-[#1f2a48] flex items-center gap-3"
          >
            <ScheduleIcon className="w-5 h-5"/>
            <span className="truncate">الحجوزات الشاملة</span>
          </a>
          <a
            href="/guests"
            onClick={(e)=>nav('guests', e)}
            className="py-2 px-3 rounded hover:bg-[#1f2a48] flex items-center gap-3"
          >
            <UserIcon className="w-5 h-5"/>
            <span className="truncate">النزلاء</span>
          </a>

          <a
            href="/vacant-rooms"
            className="py-2 px-3 rounded hover:bg-[#1f2a48] flex items-center gap-3"
          >
            <RoomTypeIcon className="w-5 h-5"/>
            <span className="truncate">الغرف الفارغة</span>
          </a>
          <a
            href="/smart-cards"
            className="py-2 px-3 rounded hover:bg-[#1f2a48] flex items-center gap-3"
          >
            <CardIcon className="w-5 h-5"/>
            <span className="truncate">البطاقات الذكية</span>
          </a>
          {/* أزيلت روابط التقويم/التسكين المنفصلة لصالح صفحة الحجوزات الشاملة */}
          <a
            href="/users"
            className="py-2 px-3 rounded hover:bg-[#1f2a48] flex items-center gap-3"
          >
            <UserIcon className="w-5 h-5"/>
            <span className="truncate">المستخدمون</span>
          </a>
          <a
            href="/settings"
            className="py-2 px-3 rounded hover:bg-[#1f2a48] flex items-center gap-3"
          >
            <SettingsIcon className="w-5 h-5"/>
            <span className="truncate">إعدادات النظام</span>
          </a>
        </nav>
      </div>
      <div className={`p-4 border-t border-[#1f2a48] flex ${collapsed ? 'flex-col items-center gap-1' : 'flex-col gap-2'}`}>
        {!collapsed && <div className="text-sm">System Administrator</div>}
        <button className="bg-red-600 hover:bg-red-700 text-white py-2 px-4 rounded text-sm w-full">خروج</button>
      </div>
    </aside>
  );
}
