import React from 'react';
import { DashboardIcon, BuildingIcon, FloorIcon, RoomTypeIcon, OccupancyIcon, CardIcon, ScheduleIcon, UserIcon, SettingsIcon } from './Icons';

export default function Sidebar({ onNavigate }) {
  const nav = (page, e) => {
    if (onNavigate) {
      e.preventDefault && e.preventDefault();
      try { console.log('Sidebar nav ->', page); } catch (_) {}
      onNavigate(page);
      // تحديث العنوان فقط بدون إعادة تحميل الصفحة
      try { window.history.pushState({}, '', `/${page}`); } catch (_) {}
    }
  };

  return (
    <aside className="bg-[#16213e] text-white w-72 min-h-screen flex flex-col justify-between shadow-xl" dir="rtl">
      <div>
        <div className="p-6 text-2xl font-bold border-b border-[#1f2a48]">فندق الكيلاني
          <div className="text-xs font-normal mt-1 text-[#ffd700]">نظام إدارة متكامل</div>
        </div>
        <nav className="mt-6 flex flex-col gap-2 px-4 text-base">
          <a href="/dashboard" onClick={(e)=>nav('dashboard', e)} className="py-2 px-3 rounded flex items-center gap-3 bg-[#1f2a48] text-[#ffd700]"><DashboardIcon className="w-5 h-5"/>لوحة التحكم</a>
          <a href="/buildings" onClick={(e)=>nav('buildings', e)} className="py-2 px-3 rounded hover:bg-[#1f2a48] flex items-center gap-3"><BuildingIcon className="w-5 h-5"/>المباني</a>
          <a href="/floors" onClick={(e)=>nav('floors', e)} className="py-2 px-3 rounded hover:bg-[#1f2a48] flex items-center gap-3"><FloorIcon className="w-5 h-5"/>الطوابق</a>
          <a href="/rooms" onClick={(e)=>nav('rooms', e)} className="py-2 px-3 rounded hover:bg-[#1f2a48] flex items-center gap-3"><RoomTypeIcon className="w-5 h-5"/>الغرف</a>
          <a href="/room-types" onClick={(e)=>nav('room-types', e)} className="py-2 px-3 rounded hover:bg-[#1f2a48] flex items-center gap-3"><RoomTypeIcon className="w-5 h-5"/>أنواع الغرف</a>
          <a href="/reservations" onClick={(e)=>nav('reservations', e)} className="py-2 px-3 rounded hover:bg-[#1f2a48] flex items-center gap-3"><ScheduleIcon className="w-5 h-5"/>الحجوزات الشاملة</a>
          <a href="/guests" onClick={(e)=>nav('guests', e)} className="py-2 px-3 rounded hover:bg-[#1f2a48] flex items-center gap-3"><UserIcon className="w-5 h-5"/>النزلاء</a>
          
          <a href="/vacant-rooms" className="py-2 px-3 rounded hover:bg-[#1f2a48] flex items-center gap-3"><RoomTypeIcon className="w-5 h-5"/>الغرف الفارغة</a>
          <a href="/smart-cards" className="py-2 px-3 rounded hover:bg-[#1f2a48] flex items-center gap-3"><CardIcon className="w-5 h-5"/>البطاقات الذكية</a>
          {/* أزيلت روابط التقويم/التسكين المنفصلة لصالح صفحة الحجوزات الشاملة */}
          <a href="/users" className="py-2 px-3 rounded hover:bg-[#1f2a48] flex items-center gap-3"><UserIcon className="w-5 h-5"/>المستخدمون</a>
          <a href="/settings" className="py-2 px-3 rounded hover:bg-[#1f2a48] flex items-center gap-3"><SettingsIcon className="w-5 h-5"/>إعدادات النظام</a>
        </nav>
      </div>
      <div className="p-4 border-t border-[#1f2a48] flex flex-col gap-2">
        <div className="text-sm">System Administrator</div>
        <button className="bg-red-600 hover:bg-red-700 text-white py-2 px-4 rounded">خروج</button>
      </div>
    </aside>
  );
}
